// get gender form profile
ProfileSex gender = project.Profile.Sex;

// send warning
if (gender == ProfileSex.Female) project.SendToLog("Oh, hell, it is a woman", ZennoLab.InterfacesLibrary.Enums.Log.LogType.Warning);

// get gender form profile
$gender = $project->Profile->Sex;

// send warning
if ($gender == ProfileSex::Female) $project->SendWarningToLog("Oh, hell, it is a woman", ZennoLab::InterfacesLibrary::Enums::Log::LogType->Warning);

// get gender form profile
ProfileSex gender = project.Profile.Sex;

// send warning
if (gender == ProfileSex.Female) project.SendToLog("Oh, hell, it is a woman", ZennoLab.InterfacesLibrary.Enums.Log.LogType.Warning, false);

// get gender form profile
$gender = $project->Profile->Sex;

// send warning
if ($gender == ProfileSex::Female) $project->SendWarningToLog("Oh, hell, it is a woman", ZennoLab::InterfacesLibrary::Enums::Log::LogType->Warning, FALSE);

                  // get gender form profile
                  ProfileSex gender = project.Profile.Sex;

                  // send warning
                  if (gender == ProfileSex.Female)
				      project.SendToLog("Oh, hell, it is a woman",
					      ZennoLab.InterfacesLibrary.Enums.Log.LogType.Warning,
						  false,
						  ZennoLab.InterfacesLibrary.Enums.Log.LogColor.Yellow);

                  // get gender form profile
                  $gender = $project->Profile->Sex;

                  // send warning
                  if ($gender == ProfileSex::Female)
				      $project->SendWarningToLog("Oh, hell, it is a woman",
					      ZennoLab::InterfacesLibrary::Enums::Log::LogType->Warning,
						  FALSE,
						  ZennoLab::InterfacesLibrary::Enums::Log::LogColor->Yellow);

