{
    // get global variable
    IGlobalVariable gv = project.GlobalVariables["MyNamespace", "MyGlobalVariable"];
}
catch (Exception e)
{
    // send error to log
    project.SendErrorToLog(e.Message, "first action OwnCode C#");
}

