// get row count
int rowCount = table.RowCount;

// if row count > 0
if (rowCount > 0) 
{
    // get row by index "0"
    var row = table.GetRow(0);

    // join items via "|"
    string result = String.Join("|", row);

    // and return this value
    return result;
}
